CREATE TABLE IF NOT EXISTS `qblog_blog` (
  `bid` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `cid` int(3) unsigned NOT NULL DEFAULT '0' COMMENT 'Category id',
  `title` varchar(180) NOT NULL DEFAULT '' COMMENT 'Blog title',
  `istop` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT 'Is top',
  `click` tinyint(4) unsigned NOT NULL DEFAULT '0' COMMENT 'Click',
  `uly_title` varchar(250) NOT NULL COMMENT 'ULY titile',
  `thumb` varchar(80) NOT NULL DEFAULT '' COMMENT 'Thumb',
  `intro` text NOT NULL COMMENT 'Introduce',
  `content` text NOT NULL COMMENT 'Content',
  `time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT 'Add time',
  `uid` varchar(50) NOT NULL DEFAULT '' COMMENT 'User id',
  `isdel` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT 'Is delete',
  PRIMARY KEY (`bid`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='Blog table' AUTO_INCREMENT=66 ;

