--
-- 表的结构 `qblog_category`
--

CREATE TABLE IF NOT EXISTS `qblog_category` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `cname` varchar(60) NOT NULL DEFAULT '' COMMENT 'Category name',
  `pid` smallint(4) unsigned NOT NULL DEFAULT '0' COMMENT 'Category pid',
  `bnums` int(10) unsigned NOT NULL DEFAULT '0' COMMENT 'Blog num',
  `keywords` varchar(60) NOT NULL DEFAULT '' COMMENT 'Category keywords',
  `seo` varchar(200) NOT NULL DEFAULT '' COMMENT 'Category seo',
  `sort` int(3) unsigned NOT NULL DEFAULT '0' COMMENT 'Category sort',
  `isoff` tinyint(1) unsigned NOT NULL DEFAULT '0' COMMENT 'Category off',
  PRIMARY KEY (`id`),
  UNIQUE KEY `catename` (`cname`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COMMENT='Category' AUTO_INCREMENT=27 ;

