(15, 'يانفۇن ئەپلىرى', 4, 1, '', 'ئىشلەتكۈچىلەرنىڭ ياخشى باھاسىغا ئېرىشكەن ئۇيغۇرچە ۋە باشقا تىلدىكى ئەپلەر', 2, 0),
(16, 'ئۇيغۇرچىلاشتۇرۇش', 4, 1, '', 'ھەر قايسى تىپتىكى تېلفۇنلارنى ئۇيغۇرچىلاشتۇرۇش تېخنىكسى تونۇشتۇرىلىدۇ.', 3, 0),
(17, 'تەرجىمە قىلىش', 4, 0, '', 'يانفۇن ئەپلىرىنى تەرجىمە قىلىش ئۇسۇللىرى تونۇشتۇرىلىدۇ.', 4, 0),
(18, 'تور بەت باشقۇرۇش', 2, 7, '', 'تور بەت باشقۇرۇش ۋە نۇرمال يۈرگۈزۈش جەرياندىكى بىلىشكە تېگىشلىك بېلىملەر يوللىنىدۇ.', 3, 0),
