(21, 'ئەتراپىمدىكى ئىشلار', 0, 0, '', '', 5, 0),
(22, 'دىيارىمىدىكى ئىشلار', 21, 2, '', 'ئانا يۇرتۇم، كىندىك قېنىم تۆكۈلگەن مەكىت ناھىيسىدىكى ئىشلار', 1, 0),
(23, 'تېخنىكا ئۆگىنەي', 21, 2, '', '', 2, 0),
