INSERT INTO `qblog_guestbook` (`id`, `pid`, `email`, `path`, `username`, `updatetime`, `ip`, `url`, `inputtime`, `content`, `verify`, `isreply`, `ord`, `status`) VALUES
(1, 0, 'karquka@qq.com', '0', 'ئاتاۋۇللا', 0, '', 'http://www.mgtsz.com', 1378867139, 'مەنمۇ نوقۇل بىر سىستىمىغىلا ئىسىلىۋالماي، مۇشۇنىڭغا ئوخشاش باشقا سىستىمىلارنىڭ ئارتۇقچىلىقىدىن پايدىلىنىشمۇ بىرخىل ھوزۇر ھىس قىلىمەن. مۇشۇنداق تىمىلارنىڭ ئۈزۈلۈپ قالماسلىقىنى تىلەيمەن. تىخىمۇ تىرىشىڭ!!!', '', 0, 0, 1),
(2, 0, '13899157112@163.com', '0', 'نۇرئەخمەتجان', 0, '', 'http://www.mgt2edu.com', 1378867225, 'مەنمۇ Windows server 2008 r2 نى ئۆگىنىش ئۈچۈن مەۋھۇم كومپيوتېر دىتالى ئىزدەپ توردىن بۇ دىتالنى چۈشۈرۈپ قاچىلىغان ئىدىم، لىكىن قانداق ئىشلىتىشنى بىلەلمەي قالدىم. ياكى مەن توردىن چۈشۈرگەن Windows server 2008 r2 نىڭ قاچىلاش ھۆججىتىدە مەسلە بارمىكىن بىلەلمىدىم. بۇ دىتالدىن پايدىلىنىپ Win7نى نۇرمال قاچىلىيالىدىم لىكىن Windows server 2008 r2 نى زادىلا قاچىلىيالمىدىم.', '', 0, 0, 1),
(3, 0, 'letnat@qq.com', '0', 'يولۋاسباي', 0, '', 'http://yolwasbay.com', 1378867329, 'ئەسسىلامۇ ئەلەيكۇم ! قەيسەر بىلوگىنى يوقلاپ كەلدۇق ۋە قەيسەر بىلوگىنىڭ بۇندىن كىيىن تىخىمۇ راۋاجلىنىشىنى ئۇمىد قىلىمىز يولۋاسباي بىلوگى قەيسەر بىلوگىنى دوستانە ئۇلىنىشقا قىتىپ قويدى مۇمكىن بولسا ئۆز ئارا ھەمكارلاشساق', '', 0, 0, 1),
(4, 0, 'uqkunxah@qq.com', '0', 'ئۇچقۇنشاھ', 0, '', 'http://www.uqkunxah.com/', 1378867395, 'تور بەت پىروگراممىسى بىلەن مۇلازىمىتىر پروگراممىسىنى ئۆزىمىز قايتىدىن قاچىلاشنىڭ ئامالى بارمىدۇ؟ ( قاچىلاش ھۈججىتى ۋە دەرسلىكىنى كۆزدە تۇتتۇم). ئەگەر ئامال بولسا، مۇشۇ ھەقتە بىر سەھىپە تەسىس قىلىپ، ئىزدەنسەك بوپتىكەن. چۈنكى مۇشۇ يېقىندا بۇ خىلدىكى مۇلازىمېتىر سەپلەنگەن مەكتەپلەر يۇقىرقى ئەھۋالغا تولا دۇچ كەپتۇ. مەن بىلەن بىرقانچىسى ئالاقىلاشتى. مەنمۇ ياردەم قىلىشقا ئاجىزلىق قىلدىم.', '', 0, 0, 1),
(5, 0, '67991373@qq.com', '0', 'مۇئەللىم', 0, '', 'http://www.muellim.me/', 1378867465, 'ئەسسالامۇ ئەلەيكۇم ، يىڭى يىلىڭىزغا مۇبارەك بولسۇن ، تېنىڭىزنىڭ سالامەت ، تۇرمۇشىڭىزنىڭ بەخىتلىك ، ئائىلىڭىزنىڭ خاتىرجەم ، ئىشلىرىڭىزنىڭ ئۇتۇقلۇق بولۇشىنى تىلەيمەن ، يىڭى يىلدا ۋە بۇندىن كېيىن بەخىت-ئامەت ، خاتىرجەملىك ، ئاسايىشلىق ، تىخىمۇ ياخشى نەتىجىلەر ۋە ياخشى دوستلار سىزگە بىر ئۆمۈر يار بولغاي ، بىلوگىڭىز تىخىمۇ گۈللەپ ياشنىسۇن !…..', '', 0, 0, 1),
(6, 0, 'abdurahman8886@hotmail.com', '0', 'ئارخان', 0, '', '', 1378867503, 'رەھمەت،قەيسەرجان.مەن تولا ھەپىلىشىپمۇ Gmail دىكى ئالاقىلاشقۇچىلارنى Outlook قا يۆتكىيەلمىگەنتىم.گەرچە gmailمۇ ھازىر ئۇيغۇرچە قوللىسىمۇ،ھەممە يېرى ئۇيغۇرچە ئەمەس.لېكىن مېنىڭ ھېس قىلىشىمچە googleنىڭ بىخەتەرلىكى ناھايىتى ياخشىكەن.ھازىر ئۇيغۇرچە win8چىقىپ ئاجايىپ ئۇلۇغ ئىش بولدى.Outlookنىڭ ھەممە يېرى ئۇيغۇرچە ئىكەن.googleنىڭ تور كۆرگۈچىسى chromeنى ئۇيغۇرچىلاشتۇرۇپ ئىشلەتسە ئۇيغۇرچە خەتلەرمۇ ناھايىتى سىلىق چىقىدىكەن.', '', 0, 0, 1),
(7, 0, '13899157112@163.com', '0', 'نۇرئەخمەتجان', 0, '', 'http://www.mgt2edu.com', 1378867648, 'مەكتەپنىڭ مۇلازىمتىر كومپيوتېردىكى ئەسلىدىكى شىئەن قاتناش ئونۋرستىتى تۈزگەن 校园网教学平台 تورى تور بەت زىيارەت قىلغۇچنىڭ ئادرىس يېزىش ئورنىغا localhost نى يازغاندا نۇرمال كۆرىنەتتى، ھەم باشقا كومپيتورلاردىمۇ بۇ تور بەت نۇرمال كۆرۈنەتتى، مەن مۇلازىمىتىر كومپييوتېرغا iis7 نى قاچىلىغان ئىدىم، شۇنىڭدىن كىين تور بەت زىيارەت قىلغۇچقا localhost نى كىرگۈزگەندە بۇ تور بەت ئېچىلماي iis7 نىڭ نۇرمال قاچىلانغانلىقنى بىلدۈرىدىغان iis7 نىڭ رەسىمى بار بەت ئېچىلىدىغان بۇلۇپ قالدى. قانداق قىلغاندا ئەسلىدىكى تور بەتنى نۇرمال ئېچىلىدىغان ھالەتكە كەلتۈرگىلى بۇلىدۇ. ( 角色ئارقىلىق 删除角色 قىلغان بولساممۇ يەنىلا ئوخشاش)', '', 0, 0, 1),
(8, 0, '465047740@qq.com', '0', 'تەۋپىق', 0, '', 'http://tawpiq.com/', 1378867837, 'ماۋۇ ئۇسلۇپ كۆركەم چىقىپتۇ مۇئەللىم نىم دىگەن بىلەن ۋەھشى ئۇسلۇپ چىقىردۇ دىسە ئۇسلۇپلىرى ئۆزگىچە مانا بىر خىل كۆركەم …\r\nسىزنىڭ ئۇسلۇبىڭىزنىڭ ئىنكاس رايونى نى قانداق مەۋجۇتقا چىقارغانسىز ۋەھشى ياخشى چىقىپتۇ دىسە.\r\nبۇنداق ئۇسسۇللارنى توردا يوللاپ ھەمبەھەرلىسىڭىز!', '', 0, 0, 1);

